package com.stackdining.www.contract;import com.stackdining.www.base.IBaseView;import com.stackdining.www.model.bean.CanteenDetailsBean;import com.stackdining.www.model.bean.DishesListBean;import com.stackdining.www.model.bean.HomeListBean;import com.stackdining.www.model.bean.NewDishesListBean;import com.stackdining.www.model.bean.PublicResultBean;import java.util.Map;/** * 契约类，方便统一管理相关接口 * <p> * <p> * 声明规范： * 1、命名规范为 "I${业务模块}Contract" 格式，如：登录模块 Contract 命名为 "ILoginContract" * 2、内部接口命名规范为 IView 、IPresenter 、IModel、IModelCallback   ps：内部接口命名不加业务名字 */public interface CanteenContract {    /**     * view层  命名必须是IView     */    interface IView extends IBaseView {        void onAboutSuccess(CanteenDetailsBean canteenDetailsBean);        void onAboutFailure(Throwable e);        void onDishesListSuccess(DishesListBean dishesListBean);        void onDishesListFailure(Throwable e);        void onSaveSuccess(PublicResultBean publicResultBean);        void onSaveFailure(Throwable e);        void onNewDishesListSuccess(NewDishesListBean newDishesListBean);        void onNewDishesListFailure(Throwable e);    }    /**     * presenter层   命名必须是IPresenter     */    interface IPresenter {        void about(Map<String, Object> paramsMap);        void dishes_list(Map<String, Object> paramsMap);        void save(Map<String, Object> paramsMap);        void new_dishes_list(Map<String, Object> paramsMap);    }    /**     * model层   命名必须是IModel     */    interface IModel {        void about(Map<String, Object> paramsMap, IModelCallback callback);        void dishes_list(Map<String, Object> paramsMap, IModelCallback callback);        void save(Map<String, Object> paramsMap, IModelCallback callback);        void new_dishes_list(Map<String, Object> paramsMap, IModelCallback callback);        /**         * model层中的接口回调         */        interface IModelCallback {            void onAboutSuccess(CanteenDetailsBean canteenDetailsBean);            void onAboutFailure(Throwable e);            void onDishesListSuccess(DishesListBean dishesListBean);            void onDishesListFailure(Throwable e);            void onSaveSuccess(PublicResultBean publicResultBean);            void onSaveFailure(Throwable e);            void onNewDishesListSuccess(NewDishesListBean newDishesListBean);            void onNewDishesListFailure(Throwable e);        }    }}