package com.stackdining.www.contract;import com.stackdining.www.base.IBaseView;import com.stackdining.www.model.bean.LoginBean;import com.stackdining.www.model.bean.PublicResultBean;import java.util.Map;/** * 契约类，方便统一管理相关接口 * <p> * <p> * 声明规范： * 1、命名规范为 "I${业务模块}Contract" 格式，如：登录模块 Contract 命名为 "ILoginContract" * 2、内部接口命名规范为 IView 、IPresenter 、IModel、IModelCallback   ps：内部接口命名不加业务名字 */public interface LoginContract {    /**     * view层  命名必须是IView     */    interface IView extends IBaseView {        void onSendVerificationCodeSuccess(PublicResultBean publicResultBean);        void onSendVerificationCodeFailure(Throwable e);        void onLoginCodeSuccess(LoginBean loginBean);        void onLoginCodeFailure(Throwable e);        void onLoginPwdSuccess(LoginBean loginBean);        void onLoginPwdFailure(Throwable e);    }    /**     * presenter层   命名必须是IPresenter     */    interface IPresenter {        void sendVerification(Map<String, Object> paramsMap);        void loginCode(Map<String, Object> paramsMap);        void loginPwd(Map<String, Object> paramsMap);    }    /**     * model层   命名必须是IModel     */    interface IModel {        void sendVerification(Map<String, Object> paramsMap, IModelCallback callback);        void loginCode(Map<String, Object> paramsMap, IModelCallback callback);        void loginPwd(Map<String, Object> paramsMap, IModelCallback callback);        /**         * model层中的接口回调         */        interface IModelCallback {            void onSendVerificationCodeSuccess(PublicResultBean publicResultBean);            void onSendVerificationCodeFailure(Throwable e);            void onLoginCodeSuccess(LoginBean loginBean);            void onLoginCodeFailure(Throwable e);            void onLoginPwdSuccess(LoginBean loginBean);            void onLoginPwdFailure(Throwable e);        }    }}